INSERT INTO historical_sites (entry_no, country, district, location, coordinates, site_type, documentation, description, visit_date, phase, source)
VALUES
('N.D.D.1', 'Nepal', 'Dailekh', 'Dullu', NULL, 'Stele', 'Alongside the Kritikhamba there were 7 other pillars of different sizes spread out throughout a gated enclosure. Measurements from the base: Pillar 1 - 30x109 / Pillar 2 - 28x131 / Pillar 3 - 22x73 / Pillar 4 - 30x119 / Pillar 5 - 29x65 / Pillar 6 - 26x67 / Pillar 7 - 27x124 / Kritikhamba - 50x266', 'The lineage of the kings of Yatse is drawn from the Dullu inscription. This stele (kīrtistambha) roughly 2.5 metres tall gives a dynastic list matching Tibetan chronicles. At the top is a Buddhist symbol and Om mani padme hum.', '2025-01-27', 1, 'Samuel Grimes / Giuseppe Tucci / Surya Mani Adhikary'),

('N.D.D.2', 'Nepal', 'Jumla', 'Michagaon', '29°16''18.1"N 82°09''24.5"E', 'Stupas / Stele', 'Michāgāũ Stupas / Stele — A total of 14 stupas spread out the complex. Stele measurements are 41x182x10.', '13 chörtens dating from 1482 to 1501 CE.', NULL, NULL, NULL),

('N.D.D.3', 'Nepal', 'Surkhet', 'Birendranagar', NULL, 'Vihara', 'Series of scattered remains around the vihara with some at the museum.', 'Kakre Vihar - oldest temple in Surkhet, rediscovered in 20th century. Built possibly in 13th century by Ashoka Challa. 200+ pillar stones found along pilgrimage route from India to Surkhet.', '2025-01-25', 1, 'Sambid Bilas Pant / T.B Harward'),

('N.D.D.4', 'Nepal', 'Surkhet', 'Bhairavsthan / Uttaraganga', 'Uttarganga', 'Pillars', 'Two pillars at Uttarganga. One tilting and venerated by locals. Second pillar split and rejoined with a rod. Horse capital at Jaleshwor temple.', 'Four pillar stones - markers of ancient route to the south.', '2025-01-26', 1, 'T.B Harward'),

('N.D.D.5', 'Nepal', 'Dailekh', 'Ramrikanda', NULL, 'Pillar', NULL, 'Impressive pillar stone nearly 2m high with seven lines of text. Yogi Naraharinath deciphered date as Sake 1037.', NULL, NULL, 'T.B Harward'),

('N.D.D.6', 'Nepal', 'Dailekh', 'Phinikanda', NULL, 'Pillars', NULL, 'Three pillar stones. First worn but upright. Second highly decorated with Lhasa image of soldier on horseback, circle, stupa, and 4.5 lines of script.', NULL, NULL, 'T.B Harward'),

('N.D.D.7', 'Nepal', 'Surkhet', 'Chaughanchaur', NULL, 'Mounds', NULL, 'Inscriptions and archaeological specimens dating to 13th century. Colophon mentions Ripu Mall as ruler in 14th century. Numerous mounds with old bricks, terracotta images, sculpted lions.', NULL, NULL, 'T.B Harward'),

('N.D.D.8', 'Nepal', 'Surkhet', 'Mangalgadhi', NULL, 'Fort', 'Found pillar with faint inscriptions mentioning Ananda Malla. Shiva temple completed 30 years ago.', 'Fort site controls movements to/from Dullu/Dailekh and India. Strategically important. One line inscription mentions Ananda Malla - possibly 15th century AD.', '2025-01-26', 1, 'T.B Harward'),

('N.D.D.9', 'Nepal', 'Surkhet', 'Province Museum', NULL, 'Pillar', 'Saw terracotta chaitya, lions as temple guardians, Buddha reliefs, Gandharan-looking head.', '4-sided pillar stone with symbolic representation of Buddha / Maitreya / Tara / Lokeshwor.', '2025-01-25', 1, 'T.B Harward'),

('N.D.D.10', 'Nepal', 'Dailekh', 'Patharnauli (Dullu)', '28.8517907, 81.6082541', 'Water Receptacle', 'Rectangular enclosure with stupa-like top. Epigraph on lintel in good condition. Structure next to current road.', 'Water receptacle made by chief-minister Devavarmii in 1334. Prthvimalla mentioned as great religious king. Colossal cistern built with cyclopean stone blocks.', '2025-01-29', 1, 'Surya Mani Adhikary / Giuseppe Tucci'),

('N.D.D.11', 'India', 'Bihar', 'Bodh Gaya', NULL, 'Copperplate Inscription', NULL, 'Ashok Challa issued inscriptions dated 1255 and 1278. Refers to himself as Khasha-Rajadhiraja (emperor of the Khashas).', NULL, NULL, 'Wikipedia'),

('N.D.D.12', 'India', 'Uttarakhand', 'Kumaon (Gopheshwar)', NULL, 'Baleshwar Temple Inscription', NULL, 'Confirmation of deed of Krachalla on reverse of copper plate grant. Inscribed Sake era 1145 (1223 AD). Krachalla invaded and subjugated Kumaon in 1223.', NULL, NULL, 'Surya Mani Adhikary'),

('N.D.D.13', 'India', 'Uttarakhand', 'Garhwal (Uttarkashi)', NULL, 'Vishwanath Temple Inscription', NULL, 'Ashoka Challa conquered Garhwal as far as Uttarkashi, evidenced by inscription in Barahat Vishwanath temple.', NULL, NULL, 'Surya Mani Adhikary'),

('N.D.D.14', 'Nepal', 'Dailekh', 'Padukasthan (Dullu)', NULL, 'Temple Complex / Vir Pillars', 'Collection of shivalingas, god reliefs, Buddha relief in bhumisparsha mudra with Krachalla inscription. 4 warrior figures on stone pillars.', 'Stone pillar inscription from Asokachalla - first recorded use of Nepali language. One of 3 centers where Agni was worshipped due to eternal flame.', '2025-01-29', 1, 'Surya Mani Adhikary'),

